-- ---------------------------
-- Table structure for characters
-- ---------------------------
CREATE TABLE IF NOT EXISTS characters (
  `account_name` varchar(45) DEFAULT NULL,
  `obj_Id` int(11) NOT NULL DEFAULT '0',
  `created_date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'use SELECT date(created_date) AS created_date FROM characters, to get only the date (YYYY-MM-DD)',
  `char_name` varchar(35) NOT NULL,
  `level` tinyint DEFAULT '1',
  `maxHp` int(11) DEFAULT '1',
  `curHp` decimal(20,2) DEFAULT '1', -- En el codigo esta como Double
  `maxCp` int(11) DEFAULT '1',
  `curCp` decimal(20,2) DEFAULT '1', -- En el codigo esta como Double
  `maxMp` int(11) DEFAULT '1',
  `curMp` decimal(20,2) DEFAULT '1', -- En el codigo esta como Double
  `acc` int(11) DEFAULT NULL,
  `crit` int(11) DEFAULT NULL,
  `evasion` int(11) DEFAULT NULL,
  `mAtk` int(11) DEFAULT NULL,
  `mDef` int(11) DEFAULT NULL,
  `mSpd` int(11) DEFAULT NULL,
  `pAtk` int(11) DEFAULT NULL,
  `pDef` int(11) DEFAULT NULL,
  `pSpd` int(11) DEFAULT NULL,
  `runSpd` int(11) DEFAULT NULL,
  `walkSpd` int(11) DEFAULT NULL,
  `str` int(11) DEFAULT NULL,
  `con` int(11) DEFAULT NULL,
  `dex` int(11) DEFAULT NULL,
  `_int` int(11) DEFAULT NULL,
  `men` int(11) DEFAULT NULL,
  `wit` int(11) DEFAULT NULL,
  `face` tinyint DEFAULT NULL,
  `hairStyle` tinyint DEFAULT NULL,
  `hairColor` tinyint DEFAULT NULL,
  `sex` tinyint(1) DEFAULT NULL,
  `heading` int(11) DEFAULT NULL,
  `x` int(11) DEFAULT NULL,
  `y` int(11) DEFAULT NULL,
  `z` int(11) DEFAULT NULL,
  `movement_multiplier` decimal(9,8) DEFAULT NULL,
  `attack_speed_multiplier` decimal(10,9) DEFAULT NULL,
  `colRad` decimal(10,3) DEFAULT NULL,
  `colHeight` decimal(10,3) DEFAULT NULL,
  `exp` bigint(20) DEFAULT NULL,
  `expBeforeDeath` bigint(20) DEFAULT '0',
  `sp` int(11) DEFAULT NULL,
  `karma` int(11) DEFAULT NULL,
  `pvpkills` int(11) DEFAULT NULL,
  `pkkills` int(11) DEFAULT NULL,
  `clanid` int(11) DEFAULT NULL,
  `maxload` int(11) DEFAULT NULL,
  `race` tinyint(2) unsigned DEFAULT NULL,
  `classid` int(11) DEFAULT NULL,
  `base_class` int(2) NOT NULL DEFAULT '0',
  `deletetime` bigint(20) DEFAULT NULL,
  `cancraft` int(11) DEFAULT NULL,
  `title` varchar(16) DEFAULT NULL,
  `rec_have` int(11) NOT NULL DEFAULT '0',
  `rec_left` int(11) NOT NULL DEFAULT '0',
  `accesslevel` int(11) unsigned DEFAULT NULL,
  `online` tinyint(1) unsigned DEFAULT NULL,
  `onlinetime` bigint(20) DEFAULT NULL,
  `char_slot` tinyint(1) unsigned DEFAULT NULL,
  `newbie` tinyint(1) unsigned DEFAULT '1',
  `lastAccess` bigint(20) DEFAULT NULL,
  `clan_privs` int(11) DEFAULT '0',
  `wantspeace` tinyint(1) unsigned DEFAULT '0',
  `isin7sdungeon` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `punish_level` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `punish_timer` bigint(20) unsigned NOT NULL DEFAULT '0',
  `power_grade` bigint(20) DEFAULT NULL,
  `nobless` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `subpledge` int(11) unsigned NOT NULL DEFAULT '0',
  `last_recom_date` bigint(20) NOT NULL DEFAULT '0',
  `lvl_joined_academy` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `apprentice` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `sponsor` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `varka_ketra_ally` tinyint(1) unsigned NOT NULL DEFAULT '0',
  `clan_join_expiry_time` bigint(20) NOT NULL DEFAULT '0',
  `clan_create_expiry_time` bigint(20) NOT NULL DEFAULT '0',
  `death_penalty_level` tinyint(3) unsigned NOT NULL DEFAULT '0',
  `pc_point` int(11) NOT NULL DEFAULT '0',
  `name_color` varchar(8) NOT NULL DEFAULT '\0\0\0',
  `title_color` varchar(8) NOT NULL DEFAULT '\0\0\0',
  `first_log` bigint(20) DEFAULT '0',
  PRIMARY KEY (`obj_Id`),
  KEY `clanid` (`clanid`)
) ;
